mapper.select:
SELECT COUNT(*) FROM F_BOOKMARK_TBL
	WHERE U_ID = #{uId }
	AND F_NO = #{fNo }

mapper.delete:
DELETE FROM F_BOOKMARK_TBL
	WHERE U_ID = #{uId }
	AND F_NO = #{fNo }

mapper.update:
UPDATE F_REPLY_TBL 
   SET F_R_CONTENT = #{ fRContent }, F_R_UPDATE = DEFAULT 
 WHERE F_R_NO = #{ fRNo }

UPDATE F_REPLY_TBL 
   SET F_R_UPDATE = DEFAULT, F_R_STATUS = 'N' 
 WHERE F_R_NO = #{ fRNo }
